import React, { PureComponent } from 'react'
import { Button, Navbar, Table } from "react-bootstrap";
import { dataService } from '../services/dataService';
import FileUpload from './FileUpload';
import LogOut from './LogOut';
import LogInPage from './LogInPage';
var jwt = require('jsonwebtoken');
global.fetch = require('node-fetch');
var AWSCognito = require('amazon-cognito-identity-js');

class UserPage extends PureComponent {
    constructor(props) {
        super(props)
        global.fetch = require('node-fetch');
        var AWSCognito = require('amazon-cognito-identity-js');
        const backendapi = {
            URL: "http://localhost:3001"
        }
        const sessionToken = sessionStorage.getItem("token")
        
        // JWT DECODE 
        // Store decoded jwt in session storage

        this.state = {
            userDataDynamo: [],
            userData: undefined,
            desc: "",
            isAdmin: false
        }
        this.setDescription = this.setDescription.bind(this)
        this.updateTable = this.updateTable.bind(this)
    }

    setDescription(d) {
        this.setState({
            desc: d
        })
    }
    getUserData(userName) {
        const backendapi = {
            URL: "http://localhost:3001"
        }
        console.log("user"+userName);
        const params = {
            method: 'GET',
            headers: { "Content-Type": "application/json" }
        }
        return fetch(`${backendapi.URL}/retrieveUserRecords/${userName}`, params).then(response => {
            console.log(response); 
            return response.json();
        })
    }

    getAdminData() {
        const backendapi = {
            URL: "http://localhost:3001"
        }
        const params = {
            method: 'GET',
            headers: { "Content-Type": "application/json" }
        }
        return fetch(`${backendapi.URL}/retrieveAdminRecords`, params).then(res => {
            console.log(res); 
            return res.json();
        })
    }
    deleteFile(fileName,id) {
        const backendapi = {
            URL: "http://localhost:3001"
        }
        const params = {
            method: 'DELETE',
            body: JSON.stringify({
                "deleteFile": fileName,
                "userId": id
            }),
            headers: { "Content-Type": "application/json" }
        }
        return fetch(`${backendapi.URL}/delete_file`, params)
    }

    getUser() {
        var poolData = {
            UserPoolId: 'us-west-1_59p3CzMOg', 
            ClientId: '6lf5mkel9kcp68hog30i0p7gtp', 
        };
        var userPool = new AWSCognito.CognitoUserPool(poolData);
        var cognitoUser = userPool.getCurrentUser();
    
        console.log("cognito user", cognitoUser);
        
        if (cognitoUser != null) {
            cognitoUser.getSession(function(err, session) {
                if (err) {
                    alert(err.message || JSON.stringify(err));
                    return;
                }
                console.log('session validity: ' + session.isValid());
        
                // NOTE: getSession must be called to authenticate user before calling getUserAttributes
                cognitoUser.getUserAttributes(function(err, attributes) {
                    if (err) {
                        // Handle error
                        console.log(err);
                    } else {
                        // Do something with attributes
                        console.log(attributes);
                    }
                });
            });
        }
    }



    updateTable2(user) {
        console.log("Called Update Table");
        this.getUserData(user)
            .then(json => {
                console.log(json);
                if (Array.isArray(json)) {
                    this.setState({
                        userDataDynamo: json
                    });
                }
            })
            .catch(reason => {
                console.log("Failed to fetch data from server, reason is : ", reason);
            });
    }
    updateTable() {
        console.log("Called Update Table");
        this.getAdminData()
            .then(json => {
                console.log(json);
                if (Array.isArray(json)) {
                    this.setState({
                        userDataDynamo: json
                    });
                }
            })
            .catch(reason => {
                console.log("Failed to fetch data from server, reason is : ", reason);
            });
    }
    componentDidMount() {
        console.log("sessionStorage");
        var token = sessionStorage.getItem("token");
        console.log(token);
        var decoded = jwt.decode(token);
        // get the decoded payload and header
        var decoded = jwt.decode(token, { complete: true });
        console.log(decoded.header);
        console.log(decoded.payload);
        const userObj = decoded.payload;
        this.setState({
            userData: userObj
        })
        const isAdmin = userObj && userObj["cognito:groups"] && userObj["cognito:groups"].filter(g => g == "admin").length > 0;

        this.setState({ isAdmin });
        setTimeout(()=> {
            console.log("UserEmailCheckBefore:"+this.state.userData.email)
            console.log("isAdmin:"+this.state.isAdmin)
            if (this.state.isAdmin){
                this.updateTable()
            } else {
                this.updateTable2(this.state.userData.email);
            }
        }, 500);
        
      
        this.getUser()
    }
    

    onClickDownLoad(file) {
        window.open("https://d2k9a10np1c61w.cloudfront.net/" + file);
    }

    onClickDelete(fileName, id) {
        this.deleteFile(fileName, id)
            .then(json => {
                console.log(json);
                setTimeout(()=> {
                if (this.state.isAdmin){
                    this.updateTable()
                } else {
                    this.updateTable2(this.state.userData.email);
                }
            }, 300);
                
            })
            .catch(reason => {
                console.log("Failed to delete, reason is : ", reason);
            });
    }

    // fetchListAgain




    render() {

        const { isAdmin } = this.state;

        return (

            <div>
                <h1>Anil</h1>
                <Navbar bg="dark" variant="dark">
                    <Navbar.Brand>DropBucket</Navbar.Brand>
                    <Navbar.Collapse className="justify-content-end">
                        <Navbar.Text>
                            Signed in as: {this.state.userData &&
                                <a href="#login">{this.state.userData.email}</a>}
                            &nbsp;&nbsp;
                            User Type: {
                                isAdmin && <a> "Administrator" </a>
                            }
                            {
                                !isAdmin && <a> "User (Non Admin)"</a>
                            }
                        </Navbar.Text>
                    </Navbar.Collapse>
                    {this.state.userData && <LogOut></LogOut>}
                    {!this.state.userData && <LogInPage></LogInPage>}
                </Navbar>
                {
                    this.state.userData &&
                    <FileUpload
                        user={this.state.userData.email}
                        desc={this.state.desc}
                        refreshList={e => this.updateTable()}
                        refreshList2={e => this.updateTable2(e)}
                        isAdmin={isAdmin}
                    >
                    </FileUpload>
                }


                <div style={{ "margin": "50px" }}>
                    <Table striped bordered hover responsive>
                        <thead>
                            <tr key={0}>
                                {
                                    isAdmin &&
                                    <th>User Name</th>
                                }

                                <th>File Name</th>
                                <th>Description</th>
                                <th>File Upload Time</th>
                                <th>File Updated Time</th>
                                <th>Download</th>
                                <th>Delete</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                this.state.userDataDynamo.map(item => {
                                    return (
                                        <tr key={item.userId}>
                                            {
                                                isAdmin &&
                                                <td>{item.userName}</td>
                                            }

                                            <td>{item.fileName}</td>
                                            <td>{item.description}</td>
                                            <td>{new Date(item.fileCreatedTime).toLocaleString()}</td>
                                            <td>{new Date(item.fileUpdatedTime).toLocaleString()}</td>
                                            <td><Button variant="outline-success" onClick={event => this.onClickDownLoad(item.fileName)}>
                                                <a href={"https://d3k2ba7dd5osnx.cloudfront.net/" + item.fileName} target="_blank" download={item.fileName}>DownLoad</a>
                                            </Button></td>
                                            <td><Button variant="outline-danger" onClick={event => this.onClickDelete(item.fileName, item.userId)}>
                                                Delete
                                            </Button></td>
                                        </tr>

                                    );
                                })
                            }

                        </tbody>
                    </Table>
                </div>

            </div>

        )
    }
}

export default UserPage
